import React from 'react';
import ArticleToolbarComponentFullScreen from './ArticleToolbarComponentLargeScreen';
import ArticleIndexBarComponent from './ArticleIndexBarComponent';
import ArticleToolBar from './ArticleToolBarComponent';
import Info from '../assets/InfoButton';
import NewArticle from '../assets/NewArticle';
import EditArticle from '../assets/EditArticle';
import Bookmark from '../assets/Bookmark';
import Tab from '../layout/Tab';
import Section from './Section';
import IndexSidePanel from '../articles/IndexSidePanel';


class Article extends React.Component<{}, State>  {
  constructor(props: any) {
    super(props);
  };

  state: State = {
    isArticleFullScreen: true
  };

  public getSidePanelProps = (isIndexSidePanelOpen: boolean) => {
    this.setState({
      isArticleFullScreen: isIndexSidePanelOpen
    });
  }

  public render() {
    // let {isArticleFullScreen} = this.state;
    // let articleClasses = 'article col-xs-10 col-sm-8 col-md-8 col-lg-8';
    // let articleToolBarClasses = 'article-toolbar-largescreens';
    // if(!isArticleFullScreen){
    //   articleClasses  = 'article col-xs-10 col-sm-8 col-md-8 col-lg-8 fullScreen';
    //   articleToolBarClasses = 'article-toolbar-largescreens fullScreen';
    // }
    return (
      <React.Fragment>
        <div className="article row">
          <ArticleIndexBarComponent getSidePanelProps={this.getSidePanelProps}/>

        <div className="col-xs-1 col-sm-2 col-md-2 col-lg-2">
          <ArticleToolbarComponentFullScreen/>
        </div>  
        <div className="article col-xs-10 col-sm-8 col-md-8 col-lg-8"> 
          <div className="article-banner">
            <span><a>USCENTCOM Medical Security Cooperation (MSC) Exchange</a></span>
          </div>
          <h1 className="article-title">USCENTCOM Medical Security Cooperation (MSC) Exchange Details</h1> 
          <div className="tag-area">
            <div className="article-tag first-tag">
              <span><a>Middle East &amp; Central Asia</a></span>
            </div>
            <div className="article-tag">
              <span><a>Infrastructure</a></span>
            </div>
            <div className="article-tag">
              <span><a>Syria</a></span>
            </div>
          </div>
          <Section/>
        <div className="col-xs-1 col-sm-2 col-md-2 col-lg-2"></div>  
        <ArticleToolBar/> 
     </div>   
        </div>
      </React.Fragment>
    );
  }
}

type State = {
  isArticleFullScreen: boolean;
};

export default Article;
