import React, {Component, createRef} from 'react';
import HomeIcon from '../assets/HomeIcon';

interface IProps{
  isOpen: boolean
}

let navPanelClasses = 'navigation-navpanel';

class NavPanel extends React.Component<MyProps> {
  constructor(props: any){
    super(props);
    this.animate = this.animate.bind(this);
  }

  public animate() {
    const {isOpen} = this.props;
    // let navPanelClasses = 'navigation-navpanel';
    if(isOpen){
      navPanelClasses = 'navigation-navpanel open';
    }
    else{
      // timer logic here to wait a few seconds to make invisible 
      navPanelClasses = 'navigation-navpanel '
    }
  }

  public render() {
    return(
      <header className="SidePanel">
        <div className="SidePanel-Component">
          <nav className={navPanelClasses} id="navigation-sidepanel">
              <ul className="sidepanel-ul">
                <li className="item">
                  <a href="/html/">Home</a>
                  <a className="item-icon" href="/html/"><HomeIcon/></a>
                </li>
                <li className="item">
                  <a href="/html/">Portals</a>
                  <a className="item-icon" href="/html/"><HomeIcon/></a>
                </li>
              </ul>
          </nav>
        </div>
      </header>
    );
  }

  }

type MyProps = {
  isOpen: boolean;
};

type Props = MyProps;

export default NavPanel;
