import React from 'react';
import {
  Container,
  Navbar,
  NavbarBrand,
} from 'reactstrap';
import PixLogoWhite from '../../components/assets/PixLogoWhite';
import Caret from '../../components/assets/Caret';
import NavDropdown from './NavDropdown';
import { DropdownMenu, DropdownItem } from 'reactstrap';

export default class Navigation extends React.Component {

  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.applySpinAnimation = this.applySpinAnimation.bind(this);
    this.state = {
      isClosed: true,
      isButtonClicked: false,
      dropdownOpen: false
    }
    this.toggleCart = this.toggleCart.bind(this);
  }

  toggleCart(){
    console.log('cart clicked');
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }

  toggle() {
    this.setState({
      // isOpen: !this.state.isOpen,
      isClosed: !this.state.isClosed
    });
    // set the current state of the navdropdown into variable
    const currentState = this.state.isClosed;
    const navdropdown = document.getElementById('navdropdown');

    if (currentState === true) {
      navdropdown.style.top = '10px';
      navdropdown.style.visibility = 'visible';
    } else {
      navdropdown.style.top = '-600px';
      navdropdown.style.visibility = 'hidden';
    }
  }

  applySpinAnimation(event) {
    const caret = document.getElementById('caret');
    console.log('test');
    caret.classList.add += "caretAnimation";
  }

  render() {
    return (<div className="Navbar-Component">
      <Navbar className="navbar" id="navbar" expand="md" fixed="top">
        <Container>
          <NavbarBrand onClick={this.toggle} className="NavBrand">
            <PixLogoWhite/>
            <Caret className="caret" onClick={this.applySpinAnimation}/>
          </NavbarBrand>
          <Nav className="ml-auto" navbar>
                        <NavItem>
                          <NavLink href="/components/">Components</NavLink>
                        </NavItem>
                        <NavItem>
                          <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
                        </NavItem>
                        <UncontrolledDropdown nav inNavbar>
                          <DropdownToggle nav caret>
                            Options
                          </DropdownToggle>
                          <DropdownMenu right>
                            <DropdownItem>
                              Option 1
                            </DropdownItem>
                            <DropdownItem>
                              Option 2
                            </DropdownItem>
                            <DropdownItem divider />
                            <DropdownItem>
                              Reset
                            </DropdownItem>
                          </DropdownMenu>
                        </UncontrolledDropdown>
                      </Nav>
        </Container>
      </Navbar>
      <NavDropdown isClosed={this.state.isClosed}/>
    </div>);
  }
}
