import React from 'react';
import {
  Button
} from 'reactstrap';
import {Form, FormGroup, Label, Input} from 'reactstrap';
// import classnames from 'classnames';
import {Container} from 'reactstrap';
import {Link} from 'react-router-dom';
import axios from 'axios';

// const validateSignUpUI = require('../../validation/validateSignUpUI');
// pass in validation module

interface IProps{
  isSubmitDisabled: boolean,
  firstName: string,
  lastName: string,
  email: string,
  password: string,
  confirmPassword: string,
  errors: array
}

export default class SignUp extends React.Component<any, any> {
  constructor(props: any) {
    super(props);
    this.state = {
      isSubmitDisabled: 'true',
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPassword: "",
      // errors: {}
    };
  }

  onSubmit = (event: React.FormEvent<HTMLInputElement>) => {
    event.preventDefault();
    const newUser = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      email: this.state.email,
      password: this.state.password,
      confirmPassword: this.state.confirmPassword
    }

    axios.post('api/users/signup', newUser).then(res => alert("Thank you for creating an account, " + res.data.firstName)).catch(err => console.log(err))
  }

  onChange = (e: React.FormEvent<HTMLInputElement>) => {
    this.setState({
      [event.target.name]: event.target.value
    });
    //pass state data to validate function
    console.log(event.target.name + " - " + event.target.value);
  }

  handleSignUpSumbit = () =>{
    alert('submit');
  }

  cancelSignUp = () => {
    alert('cancel');
  }

  render() {
    return (<div className="SignUp-Component">
      <Container>
        <Form onSubmit={this.handleSignUpSumbit}>
          <Link to="/">
            <Button>
              back
            </Button>
          </Link>
          <h1>Sign up for zas</h1>
          <FormGroup>
            <Input type="email" name="email" id="email" value={this.state.email} onChange={this.onChange} placeholder="Email address"/>
            <Label for="exampleEmail" className="validation-text" id="email-validation-signup">*Must be valid email address</Label>

            <Input name="firstName" id="firstName" value={this.state.name} onChange={this.onChange} placeholder="First name"/>
            <Label for="exampleEmail">*Must not be left blank</Label>

            <Input name="lastnName" id="lastName" value={this.state.name} onChange={this.onChange} placeholder="Last name"/>
            <Label for="exampleEmail">*Must not be left blank</Label>

            <Input type="password" name="password" id="password" value={this.state.password} onChange={this.onChange} placeholder="Create a password"/>
            <Label for="exampleEmail">*Must be valid password</Label>

            <Input type="password" name="confirmPassword" id="confirmPassword" value={this.state.confirmPassword} onChange={this.onChange} placeholder="Confirm above password"/>
            <Label for="exampleEmail">*Must match password created above</Label>
          <br></br>
          </FormGroup>

          <FormGroup>
          //where is handle
            <Button color="primary" disabled={this.state.isSubmitDisabled} onClick={this.handleSignUpSumbit}>Sign Up</Button>
            <Button color="secondary" onClick={this.cancelSignUp}>Cancel</Button>
          </FormGroup>
        </Form>
      </Container>
    </div>);
  }
}
