import React from 'react';
import PixLogoWhite from '../../components/assets/PixLogoWhite';
import PixIcon from '../../components/assets/PixLogoWhite';
import Caret from '../../components/assets/Caret';
import NavDropdown from './NavDropdown';
import { DropdownMenu, DropdownItem } from 'reactstrap';

interface IState{
  isNavDropDownButtonActivated: boolean,
}

interface IProps{
  isDropdownStateClosed: boolean,
}

export default class Navigation extends React.Component<IProps, IState> {
  constructor(props: any){
    super(props);
    this.state = {
      isNavDropDownButtonActivated: this.props.isDropdownStateClosed
    }
  }

  activateNavDropdown = () => {
    this.setState((prevState: any) => {
      return {isNavDropDownButtonActivated: !prevState.isNavDropDownButtonActivated}
    });
  }

  render() {
    const {isNavDropDownButtonActivated} = this.state;
    return (
      <header className="Navbar">
        <div className="Navbar-Component" isNavDropDownButtonActivated={isNavDropDownButtonActivated}>
          <nav className="navigation" id="navigation">
            <div className="navigation-logo" onClick= {this.activateNavDropdown}>
              <PixIcon/>
            </div>

            <div className="spacer"></div>

            <div className="navigation-items">
              <ul>
                <li><a href="/html/">Communities</a> </li>
                <li><a href="/html/">Portals</a></li>
              </ul>
            </div>
          </nav>
        </div>
      </header>
    );
  }
}
