    //   navdropdown.style.visibility = 'visible';
    // } else {
    //   navdropdown.style.left = '-600px';
    //   navdropdown.style.visibility = 'hidden';
    // }
  }

  // applySpinAnimation(event) {
  //   const caret = document.getElementById('caret');
  //   console.log('test');
  //   caret.classList.add += "caretAnimation";
  // }

  render() {
    const {isDropdownClosed} = this.state;
    return (<div className="Navbar-Component">
      <Navbar className="navbar" id="navbar" expand="md" fixed="top">
          <NavbarBrand onClick={this.toggle} className="NavBrand">
            <PixLogoWhite/>
          </NavbarBrand>
      </Navbar>
      <NavDropdown isDropdownClosed={isDropdownClosed} />
    </div>);
  }
}
