import React from 'react';
import PiXIcon from '../../components/assets/PiXIcon';
import SearchBar from '../layout/SearchBar';
import NavPanel from '../layout/NavPanel';
import Caret from '../assets/Caret';
import PiXChatIcon from '../../components/assets/PiXChatIcon';
const {menuItemsObject}= require('../../config/ConfigFunctions');
console.log(menuItemsObject);

class Navbar extends React.Component<Props, State> {
  constructor(props: any){
    super(props);
    this.toggle = this.toggle.bind(this);
    this.checkScreenWidth = this.checkScreenWidth.bind(this);
  }

  state: State = {
    isNavPanelOpen: false,
    isFeedbackOpen: false, 
    //set the state of the menu iems object 
    // menuItems: {

    // }
  };

  public checkScreenWidth(screenWidth: number, innerWidth: number): boolean {
    // let screenWidth = window.screen.width;
    // let innerWidth = window.innerWidth;
    if(screenWidth<1200 || innerWidth<1200){
      return true; 
    }
    else{
      return false;
    }
  }

  public toggle() {
    let screenWidth = window.screen.width;
    let innerWidth = window.innerWidth;
    if(screenWidth<1200 || innerWidth<1200){
      this.setState((prevState) => {
        return {
          isNavPanelOpen: !prevState.isNavPanelOpen
        }
      });
    }
  }

  public toggleFeedBack = () => {
    this.setState((prevState) => { 
      isFeedbackOpen: !prevState.isFeedbackOpen 
    });
  };

  render(){
    const { isNavPanelOpen } = this.state;
    return(
      <div className="Navbar-Component">
        {/* <NavPanel isOpen={isNavPanelOpen} menuItems={menuItems}/> */}
        <PiXIcon toggleNavPanel={this.toggle}/>
        <Caret isOpen={isNavPanelOpen}/>
        <SearchBar/>
        {/* <Menu menuItems={menuItems}/> */}
        {/*
        1. import the menu object from the function
        2. There should be logic to determine when the nav items should
        be made invisible 
        3. add the items to the navbar by mapping them to nav items
        */}
        <PiXChatIcon/>
      </div>
    );
  }
}

type MyProps = {
  loggedInPerson: {
    id: string;
    firstName: string;
    lastName: string;
    username: string;
  };
  unreadMailCount: number;
  onLogout: () => any;
};

type Props = MyProps;

type State = {
  isNavPanelOpen: boolean;
  isFeedbackOpen: boolean;
};

export default Navbar;
