import * as React from 'react';
import {Button} from 'reactstrap';
import {Form, FormGroup, Label, Input} from 'reactstrap';
import {Container} from 'reactstrap';
import {Link} from 'react-router-dom';
import axios from 'axios';

// const validateSignUpUI = require('../../validation/validateSignUpUI');
// pass in validation module

export interface IProps{
  isSubmitDisabled: string,
  firstName: string,
  lastName: string,
  email: string,
  password: string,
  confirmPassword: string,
}

interface IState {
  isSubmitDisabled: string,
  firstName: string,
  lastName: string,
  email: string,
  password: string,
  confirmPassword: string,
}

export default class SignUp extends React.Component<IProps,IState> {
  constructor(props: any) {
    super(props);
    this.state = {
      isSubmitDisabled: 'true',
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPassword: "",
      // errors: {}
    };
  }

  onSubmit = (event: React.FormEvent<HTMLInputElement>) => {
    event.preventDefault();
    const newUser = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      email: this.state.email,
      password: this.state.password,
      confirmPassword: this.state.confirmPassword
    }
    axios.post('api/users/signup', newUser).then(res => alert("Thank you for creating an account, " + res.data.firstName)).catch(err => console.log(err))
  }

  handleChange = (event: React.FormEvent<HTMLInputElement>) => {
    this.setState({

    });
    // pass state data to validate function in order to determine state of the submit button (on or off)
    // console.log(event.target.name + " - " + event.target.value);
  }

  handleSignUpSumbit = () =>{
    alert('submit');
  }

  cancelSignUp = () => {
    alert('cancel');
  }

  public render() {
    let {isSubmitDisabled, firstName, lastName, email, password, confirmPassword} = this.props;

    return (<div className="SignUp-Component">
      <Container>
        <Form onSubmit={this.handleSignUpSumbit}>
          <Link to="/">
            <Button>
              back
            </Button>
          </Link>
          <h1>Sign up for zas</h1>
          <FormGroup>
            <Input type="email" name="email" id="email" value={email} onChange={this.handleChange} placeholder="Email address"/>
            <Label for="exampleEmail" className="validation-text" id="email-validation-signup">*Must be valid email address</Label>

            <Input name="firstName" id="firstName" value={firstName} onChange={this.handleChange} placeholder="First name"/>
            <Label for="exampleEmail">*Must not be left blank</Label>

            <Input name="lastName" id="lastName" value={lastName} onChange={this.handleChange} placeholder="Last name"/>
            <Label for="exampleEmail">*Must not be left blank</Label>

            <Input type="password" name="password" id="password" value={password} handleChange={this.handleChange} placeholder="Create a password"/>
            <Label for="exampleEmail">*Must be valid password</Label>

            <Input type="password" name="confirmPassword" id="confirmPassword" value={confirmPassword} onChange={this.handleChange} placeholder="Confirm above password"/>
            <Label for="exampleEmail">*Must match password created above</Label>
          <br></br>
          </FormGroup>

          <FormGroup>
          //where is handle
            <Button color="primary" onClick={this.handleSignUpSumbit}>Sign Up</Button>
            <Button color="secondary" onClick={this.cancelSignUp}>Cancel</Button>
          </FormGroup>
        </Form>
      </Container>
    </div>);
  }
}
