import React from 'react';
import {
  Nav,
  NavItem,
  NavLink,
} from 'reactstrap';
import { Link } from 'react-router-dom';

interface IProps{
  isDropdownClosed: boolean,
}

interface IState{
  isDropdownClosed: boolean,
}

export default class NavDropdown extends React.Component<IProps, IState> {
  constructor(props: any){
    super(props);
    this.navdropdown = React.createRef();
    this.state = {
      isDropdownClosed: this.props.isDropdownClosed
    }
  }

  // refreshPage = (event: React.FormEvent<HTMLInputElement>) => {
  //   window.location.reload();
  // }


  render(){
    return (
      <div className="NavDropdown-Component" id="navdropdown">
        { /* Whats inside the chid component in the parent component */ }
        <Nav ref={this.navdropdown}>
          <NavItem className="Nav-Link">
            <Link to="/">
              <NavLink className="Navbar-Item" >Home</NavLink>
            </Link>

          </NavItem>
          <hr />

          <NavItem className="Nav-Link">
            <Link to="/signup">
              <NavLink className="Navbar-Item" >Communities</NavLink>
            </Link>
          </NavItem>

          <NavItem className="Nav-Link">
            <Link to="/login">
              <NavLink className="Navbar-Item" >Portals</NavLink>
            </Link>
          </NavItem>

          <NavItem className="Nav-Link">
            <Link to="/profile">
              <NavLink className="Navbar-Item" >RFIs</NavLink>
            </Link>
          </NavItem>

          <NavItem className="Nav-Link">
            <Link to="/trades">
              <NavLink className="Navbar-Item" >Trades</NavLink>
            </Link>
          </NavItem>

          <NavItem className="Nav-Link">
            <Link to="/messages">
              <NavLink className="Navbar-Item" >Invite New User</NavLink>
            </Link>
          </NavItem>

          <NavItem className="Nav-Link">
            <Link to="/invite">
              <NavLink className="Navbar-Item">External Applications</NavLink>
            </Link>
          </NavItem>

        </Nav>
        {/* props.children */}
      </div>

  );
}

}
