import * as React from 'react';
import {Button} from 'reactstrap';
import {Form, FormGroup, Label, Input} from 'reactstrap';
import {Container} from 'reactstrap';
import {Link} from 'react-router-dom';
import axios from 'axios';

// const validateSignUpUI = require('../../validation/validateSignUpUI');
// pass in validation module

interface IProps{
  isSubmitDisabled: boolean,
  firstName: string,
  lastName: string,
  email: string,
  password: string,
  confirmPassword: string,
}

interface IState{
  isSubmitDisabled: boolean,
  firstName: string,
  lastName: string,
  email: string,
  password: string,
  confirmPassword: string,
}

export default class SignUp extends React.Component<IProps,IState> {
  constructor(props: any) {
    super(props);
    this.state = {
      isSubmitDisabled: true,
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPassword: "",
      // errors: {}
    };
  }

  onSubmit = (event: React.FormEvent<HTMLInputElement>) => {
    event.preventDefault();
    const newUser = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      email: this.state.email,
      password: this.state.password,
      confirmPassword: this.state.confirmPassword
    }
    axios.post('api/users/signup', newUser)
    .then(res => alert("Thank you for creating an account, " + res.data.firstName))
    .catch(err => console.log(err))
  }

  public handleChange(event: any) : void {
    // this.setState({ name: event.target.value });
  }

  handleSignUpSumbit = () =>{
    alert('submit');
  }

  cancelSignUp = () => {
    alert('cancel');
  }

  handleDisableSubmitButton = () =>{

  }

  public render(): React.ReactNode {
    let {isSubmitDisabled, firstName, lastName, email, password, confirmPassword} = this.props;
    return (<div className="SignUp-Component">
      <Container>
        <Form onSubmit={this.handleSignUpSumbit}>
          <Link to="/">
            <Button>
              back
            </Button>
          </Link>
          <h1>Sign up for zas</h1>

        </Form>
      </Container>
    </div>);
  }
}
