import React from 'react';
import {
  Button,
  Collapse,
  Container,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Row,
  Col
 } from 'reactstrap';
import Logo from '../components/Logo';
import SignUpLink from './SignUpLink';
import { Link } from 'react-router-dom';
import MainSearchBar from '../components/MainSearchBar';
import Caret from '../components/Caret';
import Eyeball from '../components/Eyeball';
import Cart from '../components/Cart';

const placeholderText = 'try ';

export default class Search extends React.Component {

  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {

      placeholder: ""
    }
  }

  componentWillMount() {
    // generate new city to display in search bar
    window.onload = () => {
      const skills = ['"iphone repair"', '"graphic design"'];
      const randomSkill = Math.floor(Math.random() * skills.length);
      this.setState({
        placeholder: placeholderText + skills[randomSkill],
      });
    };
  }

  toggle() {
    this.setState({
      // isOpen: !this.state.isOpen,
      isClosed: !this.state.isClosed,
    });

    // set the current state of the sidebar into variable
    const currentState = this.state.isClosed;
    const sidebar = document.getElementById('sidebar');

    // if (currentState === true) {
    //   console.log('Sidebar is Open');
    //   sidebar.style.top = '65px';
    //   // sidebar.style.display = 'block';
    //   sidebar.style.visibility = 'visible';
    // } else {
    //   console.log('Sidebar is Closed');
    //   sidebar.style.top = '-600px';
    //   // sidebar.style.display = 'none';
    //   sidebar.style.visibility = 'hidden';
    // }
  }

  render() {
    return (
      <div>
        <Navbar className="navigation" id="navbar" color="light" light expand="md">
        <Container>
          <Row>
            <Col sm="4">
                <NavbarBrand><Link to="/"><Logo/></Link></NavbarBrand>
                <Caret onClick={this.toggle}></Caret>
                <Button className="startATrade" color="primary">start a trade</Button>{' '}
                <Eyeball></Eyeball>
                <Cart></Cart>
            </Col>
          </Row>
      </Container>
    </Navbar>
      </div>
    );
  }
}
