import React from 'react';
import {
  Container,
  Nav,
  Navbar,
  NavbarBrand,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle
} from 'reactstrap';
import PixLogoWhite from '../../components/assets/PixLogoWhite';
import Caret from '../../components/assets/Caret';
import NavDropdown from './NavDropdown';
import { DropdownMenu, DropdownItem } from 'reactstrap';


interface IState{
  isDropdownClosed: boolean,

}

export default class Navigation extends React.Component<{}, IState> {

  constructor(props:any) {
    super(props);
    this.state = {
      isDropdownClosed: true,
    }
  }

  toggle = () => {
    this.setState({
      // isOpen: !this.state.isOpen,
      isDropDownClosed: !this.state.isDropDownClosed
    });
    // set the current state of the navdropdown into variable
    const currentState = this.state.isClosed;
    const navdropdown = document.getElementById('navdropdown');

    if (currentState === true) {
      navdropdown.style.left = '10px';
      navdropdown.style.visibility = 'visible';
    } else {
      navdropdown.style.left = '-600px';
      navdropdown.style.visibility = 'hidden';
    }
  }

  // applySpinAnimation(event) {
  //   const caret = document.getElementById('caret');
  //   console.log('test');
  //   caret.classList.add += "caretAnimation";
  // }

  render() {
    return (<div className="Navbar-Component">
      <Navbar className="navbar" id="navbar" expand="md" fixed="top">
          <NavbarBrand onClick={this.toggle} className="NavBrand">
            <PixLogoWhite/>
            <Caret className="caret" onClick={this.applySpinAnimation}/>
          </NavbarBrand>
          <Nav className="ml-auto" navbar>
            <NavItem>
              <NavLink href="/components/">Communities</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="/components/">Portals</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="/components/">RFIs</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="/components/">Invite New User</NavLink>
            </NavItem>

            <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret>
                External Applications
              </DropdownToggle>
              <DropdownMenu right>
                <DropdownItem>
                  Option 1
                </DropdownItem>
                <DropdownItem>
                  Option 2
                </DropdownItem>
                <DropdownItem divider />
                <DropdownItem>
                  Reset
                </DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
          </Nav>
      </Navbar>
      <NavDropdown isClosed={this.state.isClosed} id="navdropdown"/>
    </div>);
  }
}
