import React from 'react';
import PiXIcon from '../assets/PiXIcon';
import SearchBar from './SearchBar';
import NavPanel from './NavPanel';
import Menu from './Menu';
import Caret from '../assets/Caret';
import PiXChatIcon from '../assets/PiXChatIcon';

const { getMenuItems } = require('../../config/ConfigFunctions');
const menu = getMenuItems();
const menuItemsArray = menu.menu.items;
const maxScreenWidthForNavPanel = 991;

class Navbar extends React.Component<Props, State> {
  constructor(props: any){
    super(props);
    this.toggle = this.toggle.bind(this);
  }

  state: State = {
    isNavPanelOpen: false,
    isFeedbackOpen: false, 
    items: menuItemsArray
  };

  public toggle() {
    let screenWidth = window.screen.width;
    let innerWidth = window.innerWidth;
    if(screenWidth<maxScreenWidthForNavPanel || innerWidth<maxScreenWidthForNavPanel ){
      this.setState((prevState) => {
        return {
          isNavPanelOpen: !prevState.isNavPanelOpen
        }
      });
    }
  }

  render(){
    const { isNavPanelOpen } = this.state;
    const { items } = this.state;
    return(
      <div className="Navbar-Component">
        <NavPanel isOpen={isNavPanelOpen}/>
        <PiXIcon toggleNavPanel={this.toggle}/>
        <Caret isOpen={isNavPanelOpen}/>
        <SearchBar/>
        <ul className="menu-navbar">
          <Menu items={items}/>
        </ul>
        <PiXChatIcon/>
        <div className="Discover-Component row">
          <div className="col-md-12 col-lg-12">
            <div className="communities-top">
              <div className="community-item selected">World</div> 
              <div className="community-item">Afghanistan</div> 
              <div className="community-item">Africa</div> 
              <div className="community-item">Arabian Peninsula</div> 
              <div className="community-item">Iraq</div> 
              <div className="community-item">Levant</div> 
              <div className="community-item">Multinational</div> 
              <div className="community-item">Pacific</div> 
              <div className="community-item">Syria</div> 
              <div className="community-item">Ukraine</div> 
            </div>
          </div> 
        </div>
      </div>
    );
  }
}

type MyProps = {
  loggedInPerson: {
    id: string;
    firstName: string;
    lastName: string;
    username: string;
  };
  onLogout: () => any;
};

type Props = MyProps;

type State = {
  isNavPanelOpen: boolean;
  isFeedbackOpen: boolean;
  items: [];
};

export default Navbar;
